import type { IProposalActionForm } from '@/modules/governance/components/actionComposer/actionComposer.api';
import type { IResourcesInputResource } from '@/shared/components/forms/resourcesInput';

export type IProposalActionData<
    TAction extends IProposalActionForm = IProposalActionForm,
    TMeta = undefined,
> = TAction & {
    /**
     * ID of the action generated by the form handler.
     */
    id?: string;
    /**
     * ID of the DAO.
     */
    daoId: string;
    /**
     * Additional metadata for the action.
     */
    meta: TMeta;
};

export interface ICreateProposalFormData<TMeta = undefined> {
    /**
     * Title of the proposal.
     */
    title: string;
    /**
     * Short description of the proposal.
     */
    summary: string;
    /**
     * Long description of the proposal supporting HTML tags.
     */
    body?: string;
    /**
     * Defines if the user wants to add actions to the proposal or not.
     */
    addActions: boolean;
    /**
     * Resources of the proposal.
     */
    resources: IResourcesInputResource[];
    /**
     * List of actions to be executed if the proposal succeeds.
     */
    actions: Array<IProposalActionData<IProposalActionForm, TMeta>>;
}
